import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-3430.3 Guaranteed availability of individual health insurance coverage to certain individuals with prior group coverage.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3430.3.md","filePath":"vacode/38.2-3430.3.md"}'),n={name:"vacode/38.2-3430.3.md"},t=r('<h1 id="§-38-2-3430-3-guaranteed-availability-of-individual-health-insurance-coverage-to-certain-individuals-with-prior-group-coverage" tabindex="-1">§ 38.2-3430.3 Guaranteed availability of individual health insurance coverage to certain individuals with prior group coverage. <a class="header-anchor" href="#§-38-2-3430-3-guaranteed-availability-of-individual-health-insurance-coverage-to-certain-individuals-with-prior-group-coverage" aria-label="Permalink to &quot;§ 38.2-3430.3 Guaranteed availability of individual health insurance coverage to certain individuals with prior group coverage.&quot;">​</a></h1><p>A. Guaranteed availability.</p><ol><li><p>All eligible individuals shall be provided a choice of all individual health insurance coverage currently being offered by a health insurance issuer and the chosen coverage shall be issued.</p></li><li><p>The coverage provided as required in subdivision A 1 shall not impose any preexisting condition exclusion or affiliation period with respect to the coverage.</p></li></ol><p>B. Health insurance issuers are prohibited from imposing any limitations or exclusions based upon named conditions that apply to eligible individuals.</p><p>C. Health insurance issuers shall include on all applications for health insurance coverage questions which will enable the health insurance issuer to determine if an applicant is applying for coverage as an eligible individual as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-3430.2/" target="_blank" rel="noreferrer">38.2-3430.2</a>. This requirement shall not apply to applications used in connection with managed health care plans administering and providing care to Medicare beneficiaries in exchange for preestablished compensation from Medicare, as permitted under applicable state and federal guidelines.</p><p>D. The provisions of this section shall not apply in any instance in which the provisions of this section are inconsistent or in conflict with a provision of Article 6 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-3438/" target="_blank" rel="noreferrer">38.2-3438</a> et seq.) of Chapter 34.</p><p>1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0913" target="_blank" rel="noreferrer">913</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP1004" target="_blank" rel="noreferrer">1004</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0136" target="_blank" rel="noreferrer">136</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0335" target="_blank" rel="noreferrer">335</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>.</p>',7),o=[t];function l(s,c,d,h,p,g){return i(),a("div",null,o)}const f=e(n,[["render",l]]);export{v as __pageData,f as default};
