import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-3430.4 Special rules for network plans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3430.4.md","filePath":"vacode/38.2-3430.4.md"}'),t={name:"vacode/38.2-3430.4.md"},n=r('<h1 id="§-38-2-3430-4-special-rules-for-network-plans" tabindex="-1">§ 38.2-3430.4 Special rules for network plans. <a class="header-anchor" href="#§-38-2-3430-4-special-rules-for-network-plans" aria-label="Permalink to &quot;§ 38.2-3430.4 Special rules for network plans.&quot;">​</a></h1><p>A health insurance issuer that offers health insurance coverage in the individual market may:</p><ol><li><p>Limit the eligible individuals who may be enrolled under such coverage to those who live, reside, or work within the service area for such network plan;</p></li><li><p>Within the service area of such plan, deny such coverage to such individuals if the health insurance issuer has demonstrated to the Commission that: (i) it will not have the capacity to deliver services adequately to additional individual enrollees because of its obligations to existing group contract holders, enrollees and enrollees covered under individual contracts; and (ii) it is applying this section uniformly to individuals without regard to any health status-related factor of such individuals and without regard to whether the individuals are eligible individuals;</p></li><li><p>A health insurance issuer, upon denying health insurance coverage in any service area in accordance with subdivision 2, may not offer coverage in the individual market within such service area for a period of 180 days after such coverage is denied.</p></li></ol><p>1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0913" target="_blank" rel="noreferrer">913</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0024" target="_blank" rel="noreferrer">24</a>.</p>',4),o=[n];function s(l,c,d,h,u,p){return i(),a("div",null,o)}const _=e(t,[["render",s]]);export{v as __pageData,_ as default};
