import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-3430.5 Application of financial capacity limits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3430.5.md","filePath":"vacode/38.2-3430.5.md"}'),n={name:"vacode/38.2-3430.5.md"},o=t('<h1 id="§-38-2-3430-5-application-of-financial-capacity-limits" tabindex="-1">§ 38.2-3430.5 Application of financial capacity limits. <a class="header-anchor" href="#§-38-2-3430-5-application-of-financial-capacity-limits" aria-label="Permalink to &quot;§ 38.2-3430.5 Application of financial capacity limits.&quot;">​</a></h1><p>A. A health insurance issuer may deny health insurance coverage in the individual market to an eligible individual if the health insurance issuer has demonstrated to the satisfaction of the Commission that:</p><ol><li><p>It does not have the financial reserves necessary to underwrite additional coverage; and</p></li><li><p>It is applying this section uniformly to all individuals in the individual market in the Commonwealth consistent with the laws of this Commonwealth and without regard to any health status-related factor of such individuals and without regard to whether the individuals are eligible individuals.</p></li></ol><p>B. A health insurance issuer, upon denying individual health insurance coverage in any service area in accordance with subsection A, may not offer such coverage in the individual market within such service area for a period of 180 days after the date such coverage is denied or until the health insurance issuer has demonstrated to the satisfaction of the Commission that the health insurance issuer has sufficient financial reserves to underwrite additional coverage, whichever is later.</p><p>1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0913" target="_blank" rel="noreferrer">913</a>.</p>',5),r=[o];function s(c,l,h,d,p,u){return i(),a("div",null,r)}const m=e(n,[["render",s]]);export{_ as __pageData,m as default};
