import{_ as e,o as a,c as t,O as s}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-3430.9 Regulations establishing standards.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3430.9.md","filePath":"vacode/38.2-3430.9.md"}'),i={name:"vacode/38.2-3430.9.md"},n=s('<h1 id="§-38-2-3430-9-regulations-establishing-standards" tabindex="-1">§ 38.2-3430.9 Regulations establishing standards. <a class="header-anchor" href="#§-38-2-3430-9-regulations-establishing-standards" aria-label="Permalink to &quot;§ 38.2-3430.9 Regulations establishing standards.&quot;">​</a></h1><p>A. The Commission may adopt regulations to enable it to establish and administer such standards relating to the provisions of this article and Article 5 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-3431/" target="_blank" rel="noreferrer">38.2-3431</a> et seq.) of this chapter as may be necessary to (i) implement the requirements of this article and (ii) assure that the Commonwealth&#39;s regulation of health insurance issuers is not preempted pursuant to P. L. 104-191 (The Health Insurance Portability &amp; Accountability Act of 1996).</p><p>B. The Commission may revise or amend such regulations and may increase the scope of the regulations to the extent necessary to maintain federal approval of the Commonwealth&#39;s program for regulation of health insurance issuers pursuant to the requirements established by the United States Department of Health and Human Services.</p><p>C. The Commission shall annually advise the standing committees of the General Assembly having jurisdiction over insurance matters of revisions and amendments made pursuant to subsection B.</p><p>1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0807" target="_blank" rel="noreferrer">807</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0913" target="_blank" rel="noreferrer">913</a>.</p>',5),r=[n];function o(l,h,c,d,m,u){return a(),t("div",null,r)}const _=e(i,[["render",o]]);export{g as __pageData,_ as default};
