import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-3440 Lifetime and annual limits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3440.md","filePath":"vacode/38.2-3440.md"}'),r={name:"vacode/38.2-3440.md"},n=i('<h1 id="§-38-2-3440-lifetime-and-annual-limits" tabindex="-1">§ 38.2-3440 Lifetime and annual limits. <a class="header-anchor" href="#§-38-2-3440-lifetime-and-annual-limits" aria-label="Permalink to &quot;§ 38.2-3440 Lifetime and annual limits.&quot;">​</a></h1><p>A. Notwithstanding any provision of § <a href="http://law.lis.virginia.gov/vacode/38.2-3406.1/" target="_blank" rel="noreferrer">38.2-3406.1</a>, <a href="http://law.lis.virginia.gov/vacode/38.2-3406.2/" target="_blank" rel="noreferrer">38.2-3406.2</a>, or <a href="http://law.lis.virginia.gov/vacode/38.2-3418.5/" target="_blank" rel="noreferrer">38.2-3418.5</a>, or any other section of this title to the contrary, a health carrier offering group or individual health insurance coverage shall not establish a lifetime limit on the dollar amount of essential health benefits for any covered person.</p><p>B. A health carrier shall not establish any annual limit on the dollar amount of essential health benefits for any covered person.</p><p>C. The provisions of this section shall not prevent a health carrier from placing annual or lifetime dollar limits for any covered person on specific covered benefits that are not essential health benefits to the extent that such limits are otherwise permitted under applicable federal or state law.</p><p>D. This section shall apply to any health carrier providing individual or group health insurance coverage, except that the prohibition and limits on annual limits shall not apply to a grandfathered plan providing individual health insurance coverage.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0882" target="_blank" rel="noreferrer">882</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0751" target="_blank" rel="noreferrer">751</a>.</p>',6),o=[n];function l(s,h,c,p,d,f){return a(),t("div",null,o)}const m=e(r,[["render",l]]);export{v as __pageData,m as default};
