import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-3441 Rescissions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3441.md","filePath":"vacode/38.2-3441.md"}'),o={name:"vacode/38.2-3441.md"},r=t('<h1 id="§-38-2-3441-rescissions" tabindex="-1">§ 38.2-3441 Rescissions. <a class="header-anchor" href="#§-38-2-3441-rescissions" aria-label="Permalink to &quot;§ 38.2-3441 Rescissions.&quot;">​</a></h1><p>A. Notwithstanding any provision of § <a href="http://law.lis.virginia.gov/vacode/38.2-508.5/" target="_blank" rel="noreferrer">38.2-508.5</a> or any other section of this title to the contrary, a health carrier shall not rescind coverage under a health benefit plan after an individual is covered under the plan unless the individual or a person seeking coverage on behalf of the individual performs an act, practice, or omission that constitutes fraud, or the individual makes an intentional misrepresentation of material fact, as prohibited by the terms of the plan.</p><p>B. A health carrier shall provide at least 30 days&#39; advance written notice or electronic notice to any covered person who would be affected by the proposed rescission of coverage before coverage under the plan may be rescinded, regardless of whether the rescission applies to the entire group or only to an individual within the group. Such notice shall at a minimum contain:</p><ol><li><p>Clear identification of the alleged fraudulent act, practice, or omission or the intentional misrepresentation of material fact;</p></li><li><p>An explanation as to why the act, practice, or omission was fraudulent or was an intentional misrepresentation of a material fact;</p></li><li><p>Notice that the covered person or the covered person&#39;s authorized representative, prior to the date the advance notice of the proposed rescission ends, may immediately file an internal appeal to request a reconsideration of the rescission;</p></li><li><p>A description of the health carrier&#39;s internal appeal process for rescissions, including any time limits applicable to those procedures; and</p></li><li><p>The date when the advance notice ends and the date back to which the coverage will be rescinded.</p></li></ol><p>C. The provisions of this section apply regardless of any applicable contestability period.</p><p>D. This section shall apply to any health carrier providing individual or group health insurance coverage, including any grandfathered plan.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0882" target="_blank" rel="noreferrer">882</a>.</p>',7),n=[r];function s(l,c,p,d,h,f){return a(),i("div",null,n)}const m=e(o,[["render",s]]);export{v as __pageData,m as default};
