import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-3445 Patient access to emergency services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3445.md","filePath":"vacode/38.2-3445.md"}'),a={name:"vacode/38.2-3445.md"},o=i('<h1 id="§-38-2-3445-patient-access-to-emergency-services" tabindex="-1">§ 38.2-3445 Patient access to emergency services. <a class="header-anchor" href="#§-38-2-3445-patient-access-to-emergency-services" aria-label="Permalink to &quot;§ 38.2-3445 Patient access to emergency services.&quot;">​</a></h1><p>Notwithstanding any provision of § <a href="/vacode/38.2-3407.11/">38.2-3407.11</a>, <a href="/vacode/38.2-4312.3/">38.2-4312.3</a>, or any other section of this title to the contrary, if a health carrier providing individual or group health insurance coverage provides any benefits with respect to services in an emergency department of a hospital, the health carrier shall provide coverage for emergency services:</p><ol><li><p>Without the need for any prior authorization determination, regardless of whether the emergency services are provided on an in-network or out-of-network basis;</p></li><li><p>Without regard to the final diagnosis rendered to the covered person or whether the health care provider furnishing the emergency services is a participating health care provider with respect to such services;</p></li><li><p>If such services are provided out-of-network, without imposing any administrative requirement or limitation on coverage that is more restrictive than the requirements or limitations that apply to such services received from an in-network provider;</p></li><li><p>If such services are provided out-of-network, the health carrier shall pay the out-of-network provider in accordance with § <a href="/vacode/38.2-3445.01/">38.2-3445.01</a> less any cost-sharing requirement. Any such cost-sharing requirement shall not exceed the cost-sharing requirement that would apply if such services were provided in-network as provided in § <a href="/vacode/38.2-3445.01/">38.2-3445.01</a>; and</p></li><li><p>Without regard to any term or condition of such coverage other than the exclusion of or coordination of benefits or an affiliation or waiting period.</p></li></ol><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0882" target="_blank" rel="noreferrer">882</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1080" target="_blank" rel="noreferrer">1080</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1081" target="_blank" rel="noreferrer">1081</a>.</p>',4),n=[o];function s(c,h,l,d,p,v){return r(),t("div",null,n)}const u=e(a,[["render",s]]);export{g as __pageData,u as default};
