import{_ as e,o as i,c as n,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-3544 Definition of industrial sick benefit insurance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3544.md","filePath":"vacode/38.2-3544.md"}'),a={name:"vacode/38.2-3544.md"},s=t('<h1 id="§-38-2-3544-definition-of-industrial-sick-benefit-insurance" tabindex="-1">§ 38.2-3544 Definition of industrial sick benefit insurance. <a class="header-anchor" href="#§-38-2-3544-definition-of-industrial-sick-benefit-insurance" aria-label="Permalink to &quot;§ 38.2-3544 Definition of industrial sick benefit insurance.&quot;">​</a></h1><p>Industrial sick benefit insurance means life insurance combined with accident and sickness insurance under which:</p><ol><li><p>Premiums, dues or assessments are payable weekly;</p></li><li><p>A ten dollar maximum weekly indemnity is paid to members or policyowners in the event of sickness or accident;</p></li><li><p>A $250 maximum death benefit is provided, and the named beneficiary is confined to the spouse of the insured, a relative of the insured by blood, marriage or adoption, a person bound in a pledge of marriage to the insured, or any person dependent on the insured; and</p></li><li><p>The issuing insurer is not required by its charter, bylaws, or by statute to maintain the legal reserve for death benefits.</p></li></ol><p>Code 1950, §§ 38-350, 38-351, 38-352; 1952, c. 317, § 38.1-483; 1986, c. 562.</p>',4),o=[s];function r(d,c,l,u,f,p){return i(),n("div",null,o)}const h=e(a,[["render",r]]);export{m as __pageData,h as default};
