import{_ as r,o as a,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-3570 Disclosure requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3570.md","filePath":"vacode/38.2-3570.md"}'),i={name:"vacode/38.2-3570.md"},s=e("h1",{id:"§-38-2-3570-disclosure-requirements",tabindex:"-1"},[t("§ 38.2-3570 Disclosure requirements. "),e("a",{class:"header-anchor",href:"#§-38-2-3570-disclosure-requirements","aria-label":'Permalink to "§ 38.2-3570 Disclosure requirements."'},"​")],-1),n=e("p",null,"Each health carrier shall include a description of the external review procedures in or attached to the policy, certificate, membership booklet, outline of coverage, or other evidence of coverage it provides to covered persons. The description shall include a statement that informs the covered person of his right to file a request for an external review of an adverse determination or final adverse determination with the Commission. The statement shall explain that external review is available when the adverse determination or final adverse determination involves an issue of medical necessity, appropriateness, health care setting, level of care, or effectiveness. The statement shall include the telephone number and address of the Commission. The statement shall inform the covered person that, when filing a request for an external review, the covered person will be required to authorize the release of any medical records of the covered person that may be required to be reviewed for the purpose of reaching a decision on the external review.",-1),l=e("p",null,[t("2011, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0788",target:"_blank",rel:"noreferrer"},"788"),t(".")],-1),c=[s,n,l];function d(h,f,m,p,u,v){return a(),o("div",null,c)}const x=r(i,[["render",d]]);export{g as __pageData,x as default};
