import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 38.2-3605 Coverage of preexisting conditions; Medicare supplement policies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3605.md","filePath":"vacode/38.2-3605.md"}'),a={name:"vacode/38.2-3605.md"},r=t('<h1 id="§-38-2-3605-coverage-of-preexisting-conditions-medicare-supplement-policies" tabindex="-1">§ 38.2-3605 Coverage of preexisting conditions; Medicare supplement policies. <a class="header-anchor" href="#§-38-2-3605-coverage-of-preexisting-conditions-medicare-supplement-policies" aria-label="Permalink to &quot;§ 38.2-3605 Coverage of preexisting conditions; Medicare supplement policies.&quot;">​</a></h1><p>Notwithstanding subdivision 2 (b) of § <a href="http://law.lis.virginia.gov/vacode/38.2-3503/" target="_blank" rel="noreferrer">38.2-3503</a> or the provisions of § <a href="http://law.lis.virginia.gov/vacode/38.2-3514.1/" target="_blank" rel="noreferrer">38.2-3514.1</a>, an insurer that issues a Medicare supplement policy shall not deny a claim for losses incurred more than six months from the effective date of coverage on the grounds that a condition existed prior to the effective date of coverage regardless of the application form used. Except as so provided, the policy or contract shall not include wording that would permit a defense based upon preexisting conditions.</p><p>1980, c. 204, § 38.1-362.15; 1981, c. 575; 1986, c. 562; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0522" target="_blank" rel="noreferrer">522</a>.</p>',3),n=[r];function s(c,p,d,l,f,_){return o(),i("div",null,n)}const v=e(a,[["render",s]]);export{g as __pageData,v as default};
