import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-3734 License requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3734.md","filePath":"vacode/38.2-3734.md"}'),r={name:"vacode/38.2-3734.md"},t=n('<h1 id="§-38-2-3734-license-requirements" tabindex="-1">§ 38.2-3734 License requirements. <a class="header-anchor" href="#§-38-2-3734-license-requirements" aria-label="Permalink to &quot;§ 38.2-3734 License requirements.&quot;">​</a></h1><p>Any person who, in this Commonwealth, on behalf of an insurer licensed in this Commonwealth, sells, solicits, or negotiates individual or group policies of credit life insurance shall first apply for and obtain a license from the Commission as either a life and annuities insurance agent or as a limited lines credit insurance agent as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-1800/" target="_blank" rel="noreferrer">38.2-1800</a>. Any person who, in this Commonwealth, on behalf of an insurer licensed in this Commonwealth, sells, solicits, or negotiates individual or group policies of credit accident and sickness insurance, shall first apply for and obtain a license from the Commission as either a health agent or as a limited lines credit insurance agent as defined in § <a href="http://law.lis.virginia.gov/vacode/38.2-1800/" target="_blank" rel="noreferrer">38.2-1800</a> of this title, and shall be required to be appointed to represent such insurer in this Commonwealth as set forth in § <a href="http://law.lis.virginia.gov/vacode/38.2-1833/" target="_blank" rel="noreferrer">38.2-1833</a>.</p><p>1992, c. 586; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',3),s=[t];function o(l,c,d,h,f,m){return i(),a("div",null,s)}const u=e(r,[["render",o]]);export{_ as __pageData,u as default};
