import{_ as t,o,c as s,z as e,a}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 38.2-3812 Tax on gross premium receipts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-3812.md","filePath":"vacode/38.2-3812.md"}'),n={name:"vacode/38.2-3812.md"},r=e("h1",{id:"§-38-2-3812-tax-on-gross-premium-receipts",tabindex:"-1"},[a("§ 38.2-3812 Tax on gross premium receipts. "),e("a",{class:"header-anchor",href:"#§-38-2-3812-tax-on-gross-premium-receipts","aria-label":'Permalink to "§ 38.2-3812 Tax on gross premium receipts."'},"​")],-1),i=e("p",null,"The officers of each company shall, at the time of making the annual statement, file with the Commission a sworn statement of its gross premium receipts collected from members residing in this Commonwealth for the preceding year ending December 31. Each company shall pay into the state treasury by March 1 of each year, a tax of one percent on its collected gross premiums. The tax shall be in lieu of all other taxes, state, county or municipal, based on such gross premium receipts. No city, town, municipality or other subdivision of the Commonwealth shall impose any license fee on the company or any of its agents for the privilege of conducting business in any portion of this Commonwealth. In determining such gross premium receipts, the company shall not take credit for any expenditures.",-1),c=e("p",null,"Code 1950, § 38-494; 1952, c. 317, § 38.1-511; 1986, c. 562; 1987, cc. 565, 655.",-1),m=[r,i,c];function l(p,h,d,u,f,_){return o(),s("div",null,m)}const x=t(n,[["render",l]]);export{y as __pageData,x as default};
