import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-4105 Qualifications for membership.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4105.md","filePath":"vacode/38.2-4105.md"}'),o={name:"vacode/38.2-4105.md"},s=t('<h1 id="§-38-2-4105-qualifications-for-membership" tabindex="-1">§ 38.2-4105 Qualifications for membership. <a class="header-anchor" href="#§-38-2-4105-qualifications-for-membership" aria-label="Permalink to &quot;§ 38.2-4105 Qualifications for membership.&quot;">​</a></h1><p>A. A society shall specify in its laws or rules:</p><ol><li><p>Eligibility standards for every class of membership, provided that if benefits are provided on the lives of children, the minimum age for adult membership shall be set at not less than age fifteen and not greater than age twenty-one;</p></li><li><p>The process for admission to membership for each membership class; and</p></li><li><p>The rights and privileges of each membership class, provided that only benefit members shall have the right to vote on the management of the insurance affairs of the society.</p></li></ol><p>B. A society may also admit social members who shall have no voice or vote in the management of the insurance affairs of the society.</p><p>C. Membership rights in the society are personal to the member and are not assignable.</p><p>Code 1950, §§ 38-286, 38-293, 38.1-602, 38.1-609; 1952, c. 317, § 38.1-638.29; 1964, c. 355; 1968, c. 654; 1972, cc. 530, 825; 1986, c. 562.</p>',6),r=[s];function n(c,h,l,m,p,f){return a(),i("div",null,r)}const b=e(o,[["render",n]]);export{_ as __pageData,b as default};
