import{_ as t,o as e,c as o,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-4235 Confidential treatment of information and documents.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4235.md","filePath":"vacode/38.2-4235.md"}'),a={name:"vacode/38.2-4235.md"},i=n('<h1 id="§-38-2-4235-confidential-treatment-of-information-and-documents" tabindex="-1">§ 38.2-4235 Confidential treatment of information and documents. <a class="header-anchor" href="#§-38-2-4235-confidential-treatment-of-information-and-documents" aria-label="Permalink to &quot;§ 38.2-4235 Confidential treatment of information and documents.&quot;">​</a></h1><p>All information, documents and copies obtained by or disclosed to the Commission or any other person in the course of an examination or investigation made pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-4234/" target="_blank" rel="noreferrer">38.2-4234</a>, and all information reported pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-4231/" target="_blank" rel="noreferrer">38.2-4231</a>, shall be confidential, shall not be subject to subpoena, and shall not be made public by the Commission or any other person without the prior written consent of the nonstock corporation to which they pertain. However, this provision shall not apply to information disclosed to (i) a regulatory official of any state or country; (ii) the National Association of Insurance Commissioners, its affiliate or its subsidiary; or (iii) a law-enforcement authority of any state or country. Any such disclosure by the Commission shall not constitute a waiver of confidentiality of such information. After the licensed nonstock corporation and its affiliates have been given notice and opportunity to be heard, the Commission may publish all or any part of the information and materials referred to in this section in any manner it considers appropriate, if it determines that the interests of subscribers or the public will be served by the publication.</p><p>1989, c. 606; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0519" target="_blank" rel="noreferrer">519</a>.</p>',3),r=[i];function s(c,l,d,f,h,m){return e(),o("div",null,r)}const _=t(a,[["render",s]]);export{u as __pageData,_ as default};
