import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-4401 Certain contracts, etc., not deemed plans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4401.md","filePath":"vacode/38.2-4401.md"}'),i={name:"vacode/38.2-4401.md"},r=o('<h1 id="§-38-2-4401-certain-contracts-etc-not-deemed-plans" tabindex="-1">§ 38.2-4401 Certain contracts, etc., not deemed plans. <a class="header-anchor" href="#§-38-2-4401-certain-contracts-etc-not-deemed-plans" aria-label="Permalink to &quot;§ 38.2-4401 Certain contracts, etc., not deemed plans.&quot;">​</a></h1><p>For the purposes of this chapter, the following are not deemed to be legal services plans:</p><ol><li><p>Retainer contracts made by attorneys with individual clients where fees are based on estimates of the nature and amount of services that will be provided to the specific client, and similar contracts made with a group of clients involved in the same or closely related legal matters;</p></li><li><p>Plans providing no benefits other than a limited amount of consultation and advice on simple matters either alone or in combination with referral services or on the promise of fee discounts for other matters;</p></li><li><p>Plans providing limited benefits on simple legal matters on an informal basis, not involving a legally binding promise, in the context of an employment, educational or similar relationship;</p></li><li><p>Legal services provided by unions or employee associations to their members in matters relating to employment or occupation;</p></li><li><p>Legal services provided by an agency of federal or state government or a subdivision of federal or state government to its employees; or</p></li><li><p>Legal services insurance as provided for in §§ <a href="http://law.lis.virginia.gov/vacode/38.2-127/" target="_blank" rel="noreferrer">38.2-127</a> and <a href="http://law.lis.virginia.gov/vacode/38.2-300/" target="_blank" rel="noreferrer">38.2-300</a> when provided by an insurer licensed pursuant to Chapter 10 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-1000/" target="_blank" rel="noreferrer">38.2-1000</a> et seq.) of this title.</p></li></ol><p>1978, c. 658, § 38.1-802; 1986, c. 562; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0224" target="_blank" rel="noreferrer">224</a>.</p>',4),n=[r];function s(l,c,d,p,m,h){return t(),a("div",null,n)}const _=e(i,[["render",s]]);export{v as __pageData,_ as default};
