import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-4403 The Virginia State Bar may sponsor plans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4403.md","filePath":"vacode/38.2-4403.md"}'),r={name:"vacode/38.2-4403.md"},i=o('<h1 id="§-38-2-4403-the-virginia-state-bar-may-sponsor-plans" tabindex="-1">§ 38.2-4403 The Virginia State Bar may sponsor plans. <a class="header-anchor" href="#§-38-2-4403-the-virginia-state-bar-may-sponsor-plans" aria-label="Permalink to &quot;§ 38.2-4403 The Virginia State Bar may sponsor plans.&quot;">​</a></h1><p>The Virginia State Bar may sponsor, and its member attorneys may, through a nonstock corporation, operate a legal services plan under the following conditions:</p><ol><li><p>All members of the Virginia State Bar may participate in the plan.</p></li><li><p>No more than one-fourth of the board of directors of the nonstock corporation operating the plan may be attorneys who shall be appointed to the board by the Virginia State Bar. A majority of the members of the board shall not be providers of legal services to the plan nor employees or officers of the corporation conducting the plan. The nonprovider members of the board may not be elected or appointed by the Virginia State Bar or by attorneys participating in the plan.</p></li><li><p>No part of the dues paid by attorneys to the Virginia State Bar shall be used to financially support the nonstock corporation.</p></li><li><p>The Commission shall require quarterly compliance certification from all plans licensed pursuant to this section.</p></li></ol><p>1982, c. 387, § 38.1-793.1; 1986, c. 562.</p>',4),n=[i];function s(p,l,c,h,d,m){return a(),t("div",null,n)}const y=e(r,[["render",s]]);export{f as __pageData,y as default};
