import{_ as e,o as t,c as n,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 38.2-4601.1 Title insurance agency or agent defined.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4601.1.md","filePath":"vacode/38.2-4601.1.md"}'),i={name:"vacode/38.2-4601.1.md"},o=a('<h1 id="§-38-2-4601-1-title-insurance-agency-or-agent-defined" tabindex="-1">§ 38.2-4601.1 Title insurance agency or agent defined. <a class="header-anchor" href="#§-38-2-4601-1-title-insurance-agency-or-agent-defined" aria-label="Permalink to &quot;§ 38.2-4601.1 Title insurance agency or agent defined.&quot;">​</a></h1><p>A &quot;title insurance agency or agent&quot; means any individual or business entity licensed in the Commonwealth, pursuant to Chapter 18 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-1800/" target="_blank" rel="noreferrer">38.2-1800</a> et seq.) of this title, as a title insurance agent and appointed by a title insurance company licensed in the Commonwealth, who shall perform all of the following services (for which liability arises) relevant to the issuance of title insurance policies, subject to the underwriting directives and guidelines of the agent&#39;s title insurance company. These services shall include (i) the evaluation of the title search to determine the insurability of the title; (ii) a determination of whether or not underwriting objections have been cleared; (iii) the actual issuance of a title commitment or binder and endorsements; and (iv) the actual issuance of the policy or policies and endorsements on behalf of the title insurance company. A title insurance agent holding any funds in escrow shall promptly deposit such funds in a trust account in a financial institution licensed to do business in this Commonwealth. Such trust account shall be separate from all other accounts held by the agent.</p><p>1993, c. 147; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0426" target="_blank" rel="noreferrer">426</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',3),r=[o];function s(c,l,d,h,u,f){return t(),n("div",null,r)}const _=e(i,[["render",s]]);export{p as __pageData,_ as default};
