import{_ as e,o as n,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-4700 What persons deemed insurance premium finance companies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4700.md","filePath":"vacode/38.2-4700.md"}'),r={name:"vacode/38.2-4700.md"},t=i('<h1 id="§-38-2-4700-what-persons-deemed-insurance-premium-finance-companies" tabindex="-1">§ 38.2-4700 What persons deemed insurance premium finance companies. <a class="header-anchor" href="#§-38-2-4700-what-persons-deemed-insurance-premium-finance-companies" aria-label="Permalink to &quot;§ 38.2-4700 What persons deemed insurance premium finance companies.&quot;">​</a></h1><p>A. Any person engaged in whole or in part in financing premiums for insurance on subjects of insurance resident, located or to be performed in this Commonwealth shall be an insurance premium finance company subject to this chapter. Any person who acquires agreements for this financing from an insurance premium finance company shall be deemed an insurance premium finance company subject to this chapter.</p><p>B. No person shall be deemed an insurance premium finance company by reason of any transaction lawful under the laws of this Commonwealth without regard to the provisions of this chapter. No bank, trust company, savings institution, industrial loan association, credit union, consumer finance company licensed under Chapter 15 (§ <a href="http://law.lis.virginia.gov/vacode/6.2-1500/" target="_blank" rel="noreferrer">6.2-1500</a> et seq.) of Title 6.2, licensed insurance agent extending credit as authorized in § <a href="http://law.lis.virginia.gov/vacode/38.2-1806/" target="_blank" rel="noreferrer">38.2-1806</a>, or insurer shall be licensed under the provisions of this chapter, nor be subject to the restrictions and obligations imposed by this chapter.</p><p>1964, c. 147, § 38.1-735; 1986, c. 562; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0077" target="_blank" rel="noreferrer">77</a>.</p>',4),o=[t];function s(c,p,m,d,h,l){return n(),a("div",null,o)}const _=e(r,[["render",s]]);export{f as __pageData,_ as default};
