import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 38.2-4806 Notice to insured that insurance is placed with an eligible nonadmitted insurer required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4806.md","filePath":"vacode/38.2-4806.md"}'),n={name:"vacode/38.2-4806.md"},a=r('<h1 id="§-38-2-4806-notice-to-insured-that-insurance-is-placed-with-an-eligible-nonadmitted-insurer-required" tabindex="-1">§ 38.2-4806 Notice to insured that insurance is placed with an eligible nonadmitted insurer required. <a class="header-anchor" href="#§-38-2-4806-notice-to-insured-that-insurance-is-placed-with-an-eligible-nonadmitted-insurer-required" aria-label="Permalink to &quot;§ 38.2-4806 Notice to insured that insurance is placed with an eligible nonadmitted insurer required.&quot;">​</a></h1><p>A notice in a form prescribed by the Commission shall be given to the insured under the provisions of a policy procured pursuant to this chapter by the surplus lines broker procuring the policy or by any duly licensed property and casualty insurance agent placing surplus lines business with the surplus lines broker. The notice shall contain, but not be limited to, statements that the policy is being procured from or has been placed with an insurer approved by the Commission as an eligible nonadmitted insurer and that there is no protection under the Virginia Property and Casualty Insurance Guaranty Association established under Chapter 16 (§ <a href="/vacode/38.2-1600/">38.2-1600</a> et seq.) against financial loss to claimants or policyholders because of the insolvency of an eligible nonadmitted insurer. The notice shall also set forth the name, license number, and mailing address of the broker. The notice shall be given prior to placement of the insurance. In the event coverage must be placed and become effective within 24 hours after referral of the business to the surplus lines broker, the notice may be given promptly following such a placement. In addition, a copy of the notice shall be affixed to the policy.</p><p>Code 1950, § 38.-314.7; 1960, c. 503; 1979, c. 513, § 38.1-327.52; 1981, c. 241; 1984, c. 719; 1986, c. 562; 1987, c. 519; 1988, c. 828; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0240" target="_blank" rel="noreferrer">240</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0212" target="_blank" rel="noreferrer">212</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0498" target="_blank" rel="noreferrer">498</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0205" target="_blank" rel="noreferrer">205</a>.</p>',3),o=[a];function s(l,c,d,h,u,p){return i(),t("div",null,o)}const f=e(n,[["render",s]]);export{b as __pageData,f as default};
