import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-4919 Required filing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-4919.md","filePath":"vacode/38.2-4919.md"}'),a={name:"vacode/38.2-4919.md"},o=r('<h1 id="§-38-2-4919-required-filing" tabindex="-1">§ 38.2-4919 Required filing. <a class="header-anchor" href="#§-38-2-4919-required-filing" aria-label="Permalink to &quot;§ 38.2-4919 Required filing.&quot;">​</a></h1><p>A. No CBCC provider shall engage in the business of providing or offering to provide community-based continuing care in the Commonwealth unless the CBCC provider (i) is registered with the Commission pursuant to Article 1 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-4900/" target="_blank" rel="noreferrer">38.2-4900</a> et seq.) and (ii) has filed a statement with the Commission with respect to such CBCC program as provided in subsection B.</p><p>B. A statement shall be filed with the Commission by the CBCC provider on forms prescribed by the Commission and shall include:</p><ol><li><p>All information required by the Commission pursuant to its enforcement of this article; and</p></li><li><p>The initial disclosure statement required by § <a href="http://law.lis.virginia.gov/vacode/38.2-4920/" target="_blank" rel="noreferrer">38.2-4920</a>.</p></li></ol><p>C. The statement shall be approved or disapproved in writing by the Commission within 90 days of the filing.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0208" target="_blank" rel="noreferrer">208</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0303" target="_blank" rel="noreferrer">303</a>.</p>',6),n=[o];function s(l,d,h,p,c,f){return i(),t("div",null,n)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
