import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse(`{"title":"§ 38.2-5003 Virginia Workers' Compensation Commission authorized to hear and determine claims.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-5003.md","filePath":"vacode/38.2-5003.md"}`),r={name:"vacode/38.2-5003.md"},o=t('<h1 id="§-38-2-5003-virginia-workers-compensation-commission-authorized-to-hear-and-determine-claims" tabindex="-1">§ 38.2-5003 Virginia Workers&#39; Compensation Commission authorized to hear and determine claims. <a class="header-anchor" href="#§-38-2-5003-virginia-workers-compensation-commission-authorized-to-hear-and-determine-claims" aria-label="Permalink to &quot;§ 38.2-5003 Virginia Workers&#39; Compensation Commission authorized to hear and determine claims.&quot;">​</a></h1><p>The Virginia Workers&#39; Compensation Commission is authorized to hear and pass upon all claims filed pursuant to this chapter. The Commission may exercise the power and authority granted to it in Chapter 2 of Title 65.2 as necessary to carry out the purposes of this chapter.</p><p>When a circuit court refers a civil action to the Commission pursuant to § <a href="http://law.lis.virginia.gov/vacode/8.01-273.1/" target="_blank" rel="noreferrer">8.01-273.1</a> for the purposes of determining whether the cause of action satisfies the requirements of this chapter, the Commission shall set the matter for hearing pursuant to § <a href="http://law.lis.virginia.gov/vacode/38.2-5006/" target="_blank" rel="noreferrer">38.2-5006</a>. The Commission shall communicate its decision to the referring circuit court in due course.</p><p>1987, c. 540; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0822" target="_blank" rel="noreferrer">822</a>.</p>',4),s=[o];function n(c,h,m,d,l,p){return i(),a("div",null,s)}const f=e(r,[["render",n]]);export{_ as __pageData,f as default};
