import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-5006 Hearing; parties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-5006.md","filePath":"vacode/38.2-5006.md"}'),i={name:"vacode/38.2-5006.md"},o=r('<h1 id="§-38-2-5006-hearing-parties" tabindex="-1">§ 38.2-5006 Hearing; parties. <a class="header-anchor" href="#§-38-2-5006-hearing-parties" aria-label="Permalink to &quot;§ 38.2-5006 Hearing; parties.&quot;">​</a></h1><p>A. Immediately after the Program&#39;s response is filed pursuant to subsection D of § <a href="http://law.lis.virginia.gov/vacode/38.2-5004/" target="_blank" rel="noreferrer">38.2-5004</a>, the Commission shall set the date for a hearing, which shall be held no sooner than 15 days and no later than 90 days after the filing of the Program&#39;s response, and shall notify the parties to the hearing of the time and place of such hearing. The hearing shall be held in the city or county where the birth-related neurological injury occurred, or in a contiguous city or county, unless otherwise agreed to by the parties and authorized by the Commission.</p><p>B. The parties to the hearing required under this section shall include the claimant and the Program.</p><p>1987, c. 540; 1989, c. 523; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0050" target="_blank" rel="noreferrer">50</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>.</p>',4),n=[o];function s(h,l,c,d,p,_){return a(),t("div",null,n)}const u=e(i,[["render",s]]);export{f as __pageData,u as default};
