import{_ as e,o as s,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-502 Misrepresentations and false advertising of insurance policies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-502.md","filePath":"vacode/38.2-502.md"}'),n={name:"vacode/38.2-502.md"},o=a('<h1 id="§-38-2-502-misrepresentations-and-false-advertising-of-insurance-policies" tabindex="-1">§ 38.2-502 Misrepresentations and false advertising of insurance policies. <a class="header-anchor" href="#§-38-2-502-misrepresentations-and-false-advertising-of-insurance-policies" aria-label="Permalink to &quot;§ 38.2-502 Misrepresentations and false advertising of insurance policies.&quot;">​</a></h1><p>No person shall make, issue, circulate, cause or knowingly allow to be made, issued or circulated, any estimate, illustration, circular, statement, sales presentation, omission, or comparison that:</p><ol><li><p>Misrepresents the benefits, advantages, conditions or terms of any insurance policy;</p></li><li><p>Misrepresents the dividends or share of the surplus to be received on any insurance policy;</p></li><li><p>Makes any false or misleading statements as to the dividends or share of surplus previously paid on any insurance policy;</p></li><li><p>Misrepresents or is misleading as to the financial condition of any person or the legal reserve system upon which any life insurer operates;</p></li><li><p>Uses any name or title of any insurance policy or class of insurance policies that misrepresents the true nature of the policy or policies;</p></li><li><p>Misrepresents any material fact for the purpose of inducing or tending to induce the lapse, forfeiture, exchange, conversion, replacement, or surrender of any insurance policy;</p></li><li><p>Misrepresents any material fact for the purpose of effecting a pledge, assignment, or loan on any insurance policy; or</p></li><li><p>Misrepresents any insurance policy as being a share of stock.</p></li></ol><p>Code 1950, § 38.1-52; 1952, c. 317, § 38.1-52.1; 1977, c. 529; 1978, c. 441; 1979, c. 324; 1980, c. 404; 1986, c. 562; 1990, c. 265.</p>',4),r=[o];function t(c,l,p,d,f,u){return s(),i("div",null,r)}const _=e(n,[["render",t]]);export{m as __pageData,_ as default};
