import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-511 Failure to maintain record of complaints.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-511.md","filePath":"vacode/38.2-511.md"}'),i={name:"vacode/38.2-511.md"},r=o('<h1 id="§-38-2-511-failure-to-maintain-record-of-complaints" tabindex="-1">§ 38.2-511 Failure to maintain record of complaints. <a class="header-anchor" href="#§-38-2-511-failure-to-maintain-record-of-complaints" aria-label="Permalink to &quot;§ 38.2-511 Failure to maintain record of complaints.&quot;">​</a></h1><p>No person other than agents or brokers, shall fail to maintain a complete record of all the complaints that it has received since the date of its last examination under § <a href="http://law.lis.virginia.gov/vacode/38.2-1317/" target="_blank" rel="noreferrer">38.2-1317</a>, provided that the records of complaints of a health carrier subject to Chapter 58 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-5800/" target="_blank" rel="noreferrer">38.2-5800</a> et seq.) of this title shall be retained for no less than five years. The record shall indicate the total number of complaints, their classification by line of insurance, the nature of each complaint, the disposition of these complaints, and the time it took to process each complaint.</p><p>As used in this section, &quot;complaint&quot; shall mean any written communication from a policyholder, subscriber or claimant primarily expressing a grievance.</p><p>Code 1950, § 38.1-52; 1952, c. 317, § 38.1-52.10; 1977, c. 529; 1978, c. 441; 1979, c. 324; 1980, c. 404; 1986, c. 562; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0891" target="_blank" rel="noreferrer">891</a>.</p>',4),n=[r];function c(s,l,h,p,d,m){return a(),t("div",null,n)}const u=e(i,[["render",c]]);export{_ as __pageData,u as default};
