import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-514 Failure to make disclosure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-514.md","filePath":"vacode/38.2-514.md"}'),o={name:"vacode/38.2-514.md"},t=r('<h1 id="§-38-2-514-failure-to-make-disclosure" tabindex="-1">§ 38.2-514 Failure to make disclosure. <a class="header-anchor" href="#§-38-2-514-failure-to-make-disclosure" aria-label="Permalink to &quot;§ 38.2-514 Failure to make disclosure.&quot;">​</a></h1><p>A. No person shall sell, solicit, or negotiate the sale of an annuity, a life insurance policy or an accident and sickness insurance policy without furnishing the disclosure information required by any rules and regulations of the Commission.</p><p>B. No person shall provide to an insured, claimant, subscriber or enrollee under an accident and sickness insurance policy, subscription contract, or health maintenance organization contract, an explanation of benefits which does not clearly and accurately disclose the method of benefit calculation and the actual amount which has been or will be paid to the provider of services.</p><p>Code 1950, § 38.1-52; 1952, c. 317, § 38.1-52.13; 1977, c. 529; 1978, c. 441; 1979, c. 324; 1980, c. 404; 1986, c. 562; 1991, c. 620; 1992, c. 7; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0320" target="_blank" rel="noreferrer">320</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0371" target="_blank" rel="noreferrer">371</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',4),n=[t];function c(s,l,d,u,h,p){return a(),i("div",null,n)}const m=e(o,[["render",c]]);export{f as __pageData,m as default};
