import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-515 Power of Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-515.md","filePath":"vacode/38.2-515.md"}'),a={name:"vacode/38.2-515.md"},i=r('<h1 id="§-38-2-515-power-of-commission" tabindex="-1">§ 38.2-515 Power of Commission. <a class="header-anchor" href="#§-38-2-515-power-of-commission" aria-label="Permalink to &quot;§ 38.2-515 Power of Commission.&quot;">​</a></h1><p>A. The Commission shall have power to examine and investigate the affairs of each person subject to this chapter to determine whether such person has been or is engaged in any unfair method of competition or in any unfair or deceptive act or practice prohibited by this chapter.</p><p>B. The Commission is further empowered to gather information from any person subject to this chapter relative to trade practices and whether such practices adequately and fairly serve the public interest.</p><p>C. Any person who refuses or fails to provide information in a timely manner to the Commission as provided in this section shall be subject to the enforcement and penalty provisions set forth in Chapter 2 (§ <a href="http://law.lis.virginia.gov/vacode/38.2-200/" target="_blank" rel="noreferrer">38.2-200</a> et seq.).</p><p>1952, c. 317, § 38.1-53; 1977, c. 529; 1980, c. 404; 1986, c. 562; 1991, c. 356; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0277" target="_blank" rel="noreferrer">277</a>.</p>',5),n=[i];function s(c,h,p,l,f,m){return t(),o("div",null,n)}const u=e(a,[["render",s]]);export{_ as __pageData,u as default};
