import{_ as e,o as i,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-5202 Promulgation of regulations; standards for policy provisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-5202.md","filePath":"vacode/38.2-5202.md"}'),a={name:"vacode/38.2-5202.md"},r=n('<h1 id="§-38-2-5202-promulgation-of-regulations-standards-for-policy-provisions" tabindex="-1">§ 38.2-5202 Promulgation of regulations; standards for policy provisions. <a class="header-anchor" href="#§-38-2-5202-promulgation-of-regulations-standards-for-policy-provisions" aria-label="Permalink to &quot;§ 38.2-5202 Promulgation of regulations; standards for policy provisions.&quot;">​</a></h1><p>A. The Commission may adopt regulations to establish specific standards for policy provisions of long-term care insurance policies. These standards shall be in addition to and in accordance with applicable laws of this Commonwealth. The standards shall address terms of renewability, nonforfeiture provisions if applicable, initial and subsequent conditions of eligibility, continuation or conversion, nonduplication of coverage provisions, coverage of dependents, preexisting conditions, termination of insurance, probationary periods, limitations, exceptions, reductions, elimination periods, requirements for replacement, recurrent conditions, definitions of terms, and disclosure of rating practices to consumers and may address any other standards considered appropriate by the Commission.</p><p>B. The Commission shall promulgate such regulations regarding long-term care insurance policies and certificates as it deems appropriate.</p><p>C. Regulations issued by the Commission shall:</p><ol><li><p>Recognize the unique, developing and experimental nature of long-term care insurance;</p></li><li><p>Recognize the appropriate distinctions necessary between group and individual long-term care insurance policies;</p></li><li><p>Recognize the unique needs of both those individuals who have reached retirement age and those preretirement individuals interested in purchasing long-term care insurance products; and</p></li><li><p>Recognize the appropriate distinctions necessary between long-term care insurance and accident and sickness insurance policies, prepaid health plans, and other health service plans.</p></li></ol><p>1987, c. 586; 1990, c. 285; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0114" target="_blank" rel="noreferrer">114</a>.</p>',6),s=[r];function t(c,l,p,d,u,h){return i(),o("div",null,s)}const f=e(a,[["render",t]]);export{m as __pageData,f as default};
