import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-5203 Prohibited provisions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-5203.md","filePath":"vacode/38.2-5203.md"}'),o={name:"vacode/38.2-5203.md"},a=t('<h1 id="§-38-2-5203-prohibited-provisions" tabindex="-1">§ 38.2-5203 Prohibited provisions. <a class="header-anchor" href="#§-38-2-5203-prohibited-provisions" aria-label="Permalink to &quot;§ 38.2-5203 Prohibited provisions.&quot;">​</a></h1><p>No long-term care insurance policy may:</p><ol><li><p>Be cancelled, nonrenewed, or otherwise terminated on the grounds of the age or the deterioration of the mental or physical health of the insured individual or certificateholder;</p></li><li><p>Contain a provision establishing any new waiting period in the event existing coverage is converted to or replaced by a new or other form within the same company, except with respect to an increase in benefits voluntarily selected by the insured individual or group policyholder;</p></li><li><p>Provide coverage for skilled nursing care only or provide significantly more coverage for skilled care in a facility than coverage for lower levels of care;</p></li><li><p>Be issued based on medical or health status when the policy is issued by an agent or third-party administrator pursuant to the underwriting authority granted to the agent or third-party administrator by the insurer; or</p></li><li><p>Provide that an insurer who has paid benefits under a long-term care insurance policy or certificate may recover the benefit payments in the event that the policy or certificate is rescinded.</p></li></ol><p>1987, c. 586; 1990, c. 285; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>.</p>',4),n=[a];function s(c,l,d,h,p,_){return i(),r("div",null,n)}const v=e(o,[["render",s]]);export{u as __pageData,v as default};
