import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 38.2-5509 Supplemental provisions; rules; exemption.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-5509.md","filePath":"vacode/38.2-5509.md"}'),s={name:"vacode/38.2-5509.md"},t=a('<h1 id="§-38-2-5509-supplemental-provisions-rules-exemption" tabindex="-1">§ 38.2-5509 Supplemental provisions; rules; exemption. <a class="header-anchor" href="#§-38-2-5509-supplemental-provisions-rules-exemption" aria-label="Permalink to &quot;§ 38.2-5509 Supplemental provisions; rules; exemption.&quot;">​</a></h1><p>A. The provisions of this Act are supplemental to any other provisions of the laws of this Commonwealth, and shall not preclude or limit any other powers or duties of the Commission, the Commissioner of Insurance, or any of the Commission&#39;s employees or agents under such laws, including, but not limited to, the provisions of §§ <a href="/vacode/38.2-1038/">38.2-1038</a> and <a href="/vacode/38.2-1040/">38.2-1040</a>, subdivision A 7 of § <a href="/vacode/38.2-4316/">38.2-4316</a>, and Chapter 15 (§ <a href="/vacode/38.2-1500/">38.2-1500</a> et seq.) and any regulations issued thereunder.</p><p>B. The Commission may adopt reasonable rules necessary for the implementation of this Act.</p><p>C. The Commission may exempt from the application of this Act any domestic property and casualty insurer which:</p><ol><li><p>Writes direct business only in this Commonwealth;</p></li><li><p>Writes direct annual premiums of $2 million or less; and</p></li><li><p>Assumes no reinsurance in excess of five percent of direct premium written.</p></li></ol><p>D. The Commission may exempt from the application of this Act an insurer organized and operating under the laws of this Commonwealth and licensed pursuant to the provisions of Chapter 25 (§ <a href="/vacode/38.2-2500/">38.2-2500</a> et seq.).</p><p>E. The Commission may exempt from the application of this Act a domestic health organization that writes direct business only in this Commonwealth and assumes no reinsurance in excess of five percent of direct premium written, and either (i) writes direct annual premiums of two million dollars or less for comprehensive medical coverages or (ii) is licensed pursuant to Chapter 45 (§ <a href="/vacode/38.2-4500/">38.2-4500</a> et seq.) and covers less than 2,000 lives. As used in this subsection, &quot;comprehensive medical coverages&quot; means contracts providing basic health care services and Medicare and Medicaid risk coverages or policies providing hospital, surgical, major medical, Medicare risk and Medicaid risk coverages. Medicare supplement need not be included and premiums for administrative services shall not be included.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0789" target="_blank" rel="noreferrer">789</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0047" target="_blank" rel="noreferrer">47</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>.</p>',8),r=[t];function n(l,p,c,m,d,h){return i(),o("div",null,r)}const v=e(s,[["render",n]]);export{f as __pageData,v as default};
