import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-5604 Health savings accounts exempt from claims.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-5604.md","filePath":"vacode/38.2-5604.md"}'),i={name:"vacode/38.2-5604.md"},n=o('<h1 id="§-38-2-5604-health-savings-accounts-exempt-from-claims" tabindex="-1">§ 38.2-5604 Health savings accounts exempt from claims. <a class="header-anchor" href="#§-38-2-5604-health-savings-accounts-exempt-from-claims" aria-label="Permalink to &quot;§ 38.2-5604 Health savings accounts exempt from claims.&quot;">​</a></h1><p>A. As used in this section, &quot;health savings account&quot; means a health savings account or medical savings account authorized under § 220 or 223 of the Internal Revenue Code of 1986, as amended from time to time.</p><p>B. Notwithstanding any provision of law to the contrary, the rights of a participant or beneficiary of a health savings account to hold or to receive moneys paid into or out of, the assets of, and the income of the health savings account:</p><ol><li><p>Shall be exempt from creditor process;</p></li><li><p>Shall not be liable to attachment, garnishment, or other process; and</p></li><li><p>Shall not be seized, taken, appropriated, or applied by any legal or equitable process or operation of law to pay any debt or liability of the participant or beneficiary of the account.</p></li></ol><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0595" target="_blank" rel="noreferrer">595</a>.</p>',5),s=[n];function r(c,l,h,p,m,d){return a(),t("div",null,s)}const u=e(i,[["render",r]]);export{_ as __pageData,u as default};
