import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 38.2-6004 Reporting requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-6004.md","filePath":"vacode/38.2-6004.md"}'),a={name:"vacode/38.2-6004.md"},n=t('<h1 id="§-38-2-6004-reporting-requirements" tabindex="-1">§ 38.2-6004 Reporting requirements. <a class="header-anchor" href="#§-38-2-6004-reporting-requirements" aria-label="Permalink to &quot;§ 38.2-6004 Reporting requirements.&quot;">​</a></h1><p>A. Each licensee under this chapter shall file with the Commission on or before March 1 of each year the certification required by subsection E of § <a href="http://law.lis.virginia.gov/vacode/38.2-6011/" target="_blank" rel="noreferrer">38.2-6011</a> and an annual report containing such information as the Commission may prescribe by rule or regulation; however, such information shall be limited to only those transactions where the viator is a resident of this Commonwealth.</p><p>B. A viatical settlement provider shall report in writing to the Commission the following:</p><ol><li><p>New or revised information about its officers, stockholders owning 10 percent or greater interest in the licensee or an affiliate of the licensee, partners, directors, members, or designated employees within 30 calendar days of the change;</p></li><li><p>Any change in business or residence address or name within 30 calendar days of the change.</p></li></ol><p>C. A licensed viatical settlement provider convicted of a felony shall report within 30 calendar days to the Commission the facts and circumstances regarding the criminal conviction.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0814" target="_blank" rel="noreferrer">814</a>, § 38.2-5703; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0059" target="_blank" rel="noreferrer">59</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>.</p>',6),o=[n];function s(l,c,h,p,d,f){return r(),i("div",null,o)}const _=e(a,[["render",s]]);export{m as __pageData,_ as default};
