import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 38.2-617 Individual remedies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-617.md","filePath":"vacode/38.2-617.md"}'),o={name:"vacode/38.2-617.md"},n=i('<h1 id="§-38-2-617-individual-remedies" tabindex="-1">§ 38.2-617 Individual remedies. <a class="header-anchor" href="#§-38-2-617-individual-remedies" aria-label="Permalink to &quot;§ 38.2-617 Individual remedies.&quot;">​</a></h1><p>A. If any insurance institution, agent, or insurance-support organization fails to comply with §§ <a href="/vacode/38.2-608/">38.2-608</a>, <a href="/vacode/38.2-609/">38.2-609</a>, or § <a href="/vacode/38.2-610/">38.2-610</a>, any person whose rights granted under those sections are violated may apply to a court of competent jurisdiction for appropriate equitable relief.</p><p>B. An insurance institution, agent, or insurance-support organization that discloses information in violation of § <a href="/vacode/38.2-613/">38.2-613</a> shall be liable for damages sustained by the individual to whom the information relates. No individual, however, shall be entitled to a monetary award that exceeds the actual damages sustained by the individual as a result of a violation of § <a href="/vacode/38.2-613/">38.2-613</a>.</p><p>C. In any action brought pursuant to this section, the court may award the cost of the action and reasonable attorney&#39;s fees to the prevailing party.</p><p>D. An action under this section must be brought within two years from the date the alleged violation is or should have been discovered.</p><p>E. Except as specifically provided in this section, there shall be no remedy or recovery available to individuals, in law or in equity, for occurrences constituting a violation of any provision of this article.</p><p>1981, c. 389, § 38.1-57.24; 1986, c. 562; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0264" target="_blank" rel="noreferrer">264</a>.</p>',7),r=[n];function s(d,c,l,h,p,u){return a(),t("div",null,r)}const _=e(o,[["render",s]]);export{v as __pageData,_ as default};
