import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 38.2-6507 Appeal of decertification or denial of certification.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-6507.md","filePath":"vacode/38.2-6507.md"}'),o={name:"vacode/38.2-6507.md"},r=t('<h1 id="§-38-2-6507-appeal-of-decertification-or-denial-of-certification" tabindex="-1">§ 38.2-6507 Appeal of decertification or denial of certification. <a class="header-anchor" href="#§-38-2-6507-appeal-of-decertification-or-denial-of-certification" aria-label="Permalink to &quot;§ 38.2-6507 Appeal of decertification or denial of certification.&quot;">​</a></h1><p>A. The Exchange shall give each health carrier the opportunity to appeal a decertification decision or the denial of certification as a qualified health plan or qualified dental plan.</p><p>B. The Exchange shall give each health carrier that appeals a decertification decision or the denial of certification the opportunity for:</p><ol><li><p>The submission and consideration of facts, arguments, or proposals of adjustment of the plan or plans at issue; and</p></li><li><p>A hearing and a decision on the record, to the extent that the Exchange and the health carrier are unable to reach agreement following the submission of the information in subdivision 1.</p></li></ol><p>C. Any hearing held pursuant to subsection B shall be conducted by the Commission in accordance with its rules of practice and procedure.</p><p>D. Any final action or order of the Commission shall be subject to judicial review in accordance with the provisions of §§ <a href="/vacode/12.1-39/">12.1-39</a>, <a href="/vacode/12.1-40/">12.1-40</a>, and <a href="/vacode/12.1-41/">12.1-41</a>.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0916" target="_blank" rel="noreferrer">916</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0917" target="_blank" rel="noreferrer">917</a>.</p>',7),n=[r];function c(l,s,d,h,f,p){return a(),i("div",null,n)}const g=e(o,[["render",c]]);export{u as __pageData,g as default};
