import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-6511 Procurement, contracting, and personnel.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-6511.md","filePath":"vacode/38.2-6511.md"}'),o={name:"vacode/38.2-6511.md"},r=n('<h1 id="§-38-2-6511-procurement-contracting-and-personnel" tabindex="-1">§ 38.2-6511 Procurement, contracting, and personnel. <a class="header-anchor" href="#§-38-2-6511-procurement-contracting-and-personnel" aria-label="Permalink to &quot;§ 38.2-6511 Procurement, contracting, and personnel.&quot;">​</a></h1><p>A. The Commission may contract with other eligible entities and enter into memoranda of understanding with other agencies of the Commonwealth to carry out any of the functions of the Exchange, including agreements with other states or federal agencies to perform joint administrative functions. Such contracts are not subject to the Virginia Public Procurement Act (§ <a href="/vacode/2.2-4300/">2.2-4300</a> et seq.).</p><p>B. The Exchange shall not enter into contracts with any health carrier or an affiliate of a health carrier.</p><p>C. Employees of the Exchange shall be (i) exempt from application of the Virginia Personnel Act (§ <a href="/vacode/2.2-2900/">2.2-2900</a> et seq.) and Chapter 30 (§ <a href="/vacode/2.2-3000/">2.2-3000</a> et seq.) of Title 2.2, as hereinafter amended or recodified, to the same extent as other employees of the Commission; (ii) eligible for participation in the Virginia Retirement System to the same extent as other similarly situated employees of the Commission; and (iii) compensated and managed in accordance with the Commission&#39;s practices and policies applicable to all Commission employees.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0916" target="_blank" rel="noreferrer">916</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0917" target="_blank" rel="noreferrer">917</a>.</p>',5),i=[r];function c(s,l,h,m,d,p){return t(),a("div",null,i)}const g=e(o,[["render",c]]);export{_ as __pageData,g as default};
