import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 38.2-813 What constitutes appointment of agent for service of process.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/38.2-813.md","filePath":"vacode/38.2-813.md"}'),i={name:"vacode/38.2-813.md"},n=a('<h1 id="§-38-2-813-what-constitutes-appointment-of-agent-for-service-of-process" tabindex="-1">§ 38.2-813 What constitutes appointment of agent for service of process. <a class="header-anchor" href="#§-38-2-813-what-constitutes-appointment-of-agent-for-service-of-process" aria-label="Permalink to &quot;§ 38.2-813 What constitutes appointment of agent for service of process.&quot;">​</a></h1><p>The Clerk of the Commission shall be deemed to be appointed by any unlicensed public adjuster as its agent for the service of process pursuant to § <a href="http://law.lis.virginia.gov/vacode/13.1-758/" target="_blank" rel="noreferrer">13.1-758</a> if any of the following acts are effected by mail or otherwise in the Commonwealth by such unlicensed public adjuster: (i) the investigation, negotiation, adjustment, or provision of advice to insureds in relation to first party claims arising under insurance contracts that insure real or personal property located in the Commonwealth; (ii) the solicitation of public adjusting for such contracts; (iii) the collection of fees, commissions, salaries, or other considerations for such contracts; or (iv) the transaction of any other insurance business in connection with such contracts.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0735" target="_blank" rel="noreferrer">735</a>.</p>',3),r=[n];function s(c,l,f,p,h,d){return t(),o("div",null,r)}const m=e(i,[["render",s]]);export{_ as __pageData,m as default};
