import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 4.1-101.1 Certified mail; subsequent mail or notices may be sent by regular mail; electronic communications as alternative to regular mail; limitation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-101.1.md","filePath":"vacode/4.1-101.1.md"}'),r={name:"vacode/4.1-101.1.md"},o=a('<h1 id="§-4-1-101-1-certified-mail-subsequent-mail-or-notices-may-be-sent-by-regular-mail-electronic-communications-as-alternative-to-regular-mail-limitation" tabindex="-1">§ 4.1-101.1 Certified mail; subsequent mail or notices may be sent by regular mail; electronic communications as alternative to regular mail; limitation. <a class="header-anchor" href="#§-4-1-101-1-certified-mail-subsequent-mail-or-notices-may-be-sent-by-regular-mail-electronic-communications-as-alternative-to-regular-mail-limitation" aria-label="Permalink to &quot;§ 4.1-101.1 Certified mail; subsequent mail or notices may be sent by regular mail; electronic communications as alternative to regular mail; limitation.&quot;">​</a></h1><p>A. Whenever in this subtitle the Board is required to send any mail or notice by certified mail and such mail or notice is sent certified mail, return receipt requested, then any subsequent, identical mail or notice that is sent by the Board may be sent by regular mail.</p><p>B. Except as provided in subsection C, whenever in this subtitle the Board is required or permitted to send any mail, notice, or other official communication by regular mail to persons licensed under Chapter 2 (§ <a href="http://law.lis.virginia.gov/vacode/4.1-200/" target="_blank" rel="noreferrer">4.1-200</a> et seq.), upon the request of a licensee, the Board may instead send such mail, notice, or official communication by email, text message, or other electronic means to the email address, telephone number, or other contact information provided to the Board by the licensee, provided that the Board retains sufficient proof of the electronic delivery, which may be an electronic receipt of delivery or a certificate of service prepared by the Board confirming the electronic delivery.</p><p>C. No notice required by § <a href="http://law.lis.virginia.gov/vacode/4.1-227/" target="_blank" rel="noreferrer">4.1-227</a> to (i) a licensee of a hearing that may result in the suspension or revocation of his license or the imposition of a civil penalty or (ii) a person holding a permit shall be sent by the Board by email, text message, or other electronic means, nor shall any decision by the Board to suspend or revoke a license or permit or impose a civil penalty be sent by the Board by email, text message, or other electronic means.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0566" target="_blank" rel="noreferrer">566</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>.</p>',5),n=[o];function l(s,c,m,d,h,u){return i(),t("div",null,n)}const f=e(r,[["render",l]]);export{b as __pageData,f as default};
