import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 4.1-103.01 Additional powers; access to certain tobacco sales records; inspections; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-103.01.md","filePath":"vacode/4.1-103.01.md"}'),s={name:"vacode/4.1-103.01.md"},i=t('<h1 id="§-4-1-103-01-additional-powers-access-to-certain-tobacco-sales-records-inspections-penalty" tabindex="-1">§ 4.1-103.01 Additional powers; access to certain tobacco sales records; inspections; penalty. <a class="header-anchor" href="#§-4-1-103-01-additional-powers-access-to-certain-tobacco-sales-records-inspections-penalty" aria-label="Permalink to &quot;§ 4.1-103.01 Additional powers; access to certain tobacco sales records; inspections; penalty.&quot;">​</a></h1><p>A. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/58.1-3/" target="_blank" rel="noreferrer">58.1-3</a> or any other provision of law, the Tax Commissioner shall provide to the Board the name, address, and other identifying information within his possession of all wholesale cigarette dealers.</p><p>B. All invoices, books, papers or other memoranda and records concerning the sale of cigarettes maintained by wholesale cigarette dealers pursuant to § <a href="http://law.lis.virginia.gov/vacode/58.1-1007/" target="_blank" rel="noreferrer">58.1-1007</a> shall be subject to inspection during normal business hours by special agents of the Board. Any person who, upon request by a special agent, unreasonably fails or refuses to allow an inspection of the records authorized by this subsection shall be guilty of a Class 2 misdemeanor.</p><p>C. The Board may use the information obtained from the Tax Commissioner or by the inspections authorized by subsection B only for the purpose of creating and maintaining a list of retail dealers to facilitate enforcement of the laws governing the sale of tobacco products to minors. Neither the Board nor any special agent shall divulge any information provided by the Tax Commissioner or obtained in the performance of the inspections authorized by subsection B to anyone other than to another special agent. Any person violating the provisions of this subsection shall be guilty of a Class 2 misdemeanor.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0189" target="_blank" rel="noreferrer">189</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0364" target="_blank" rel="noreferrer">364</a>.</p>',5),n=[i];function r(c,l,d,p,h,f){return o(),a("div",null,n)}const m=e(s,[["render",r]]);export{b as __pageData,m as default};
