import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 4.1-1100 Possession, etc., of marijuana and marijuana products by persons 21 years of age or older lawful; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-1100.md","filePath":"vacode/4.1-1100.md"}'),s={name:"vacode/4.1-1100.md"},r=n('<h1 id="§-4-1-1100-possession-etc-of-marijuana-and-marijuana-products-by-persons-21-years-of-age-or-older-lawful-penalties" tabindex="-1">§ 4.1-1100 Possession, etc., of marijuana and marijuana products by persons 21 years of age or older lawful; penalties. <a class="header-anchor" href="#§-4-1-1100-possession-etc-of-marijuana-and-marijuana-products-by-persons-21-years-of-age-or-older-lawful-penalties" aria-label="Permalink to &quot;§ 4.1-1100 Possession, etc., of marijuana and marijuana products by persons 21 years of age or older lawful; penalties.&quot;">​</a></h1><p>A. Except as otherwise provided in this subtitle and notwithstanding any other provision of law, a person 21 years of age or older may lawfully possess on his person or in any public place not more than one ounce of marijuana or an equivalent amount of marijuana product as determined by regulation promulgated by the Board.</p><p>B. Any person who possesses on his person or in any public place marijuana or marijuana products in excess of the amounts set forth in subsection A is subject to a civil penalty of no more than $25. The penalty for any violations of this section by an adult shall be prepayable according to the procedures in § <a href="/vacode/16.1-69.40:2/">16.1-69.40:2</a>.</p><p>C. With the exception of a licensee in the course of his duties related to such licensee&#39;s marijuana establishment, any person who possesses on his person or in any public place more than one pound of marijuana or an equivalent amount of marijuana product as determined by regulation promulgated by the Board is guilty of a felony punishable by a term of imprisonment of not less than one year nor more than 10 years and a fine of not more than $250,000, or both.</p><p>D. The provisions of this section shall not apply to members of federal, state, county, city, or town law-enforcement agencies, jail officers, or correctional officers, as defined in § <a href="/vacode/53.1-1/">53.1-1</a>, certified as handlers of dogs trained in the detection of controlled substances when possession of marijuana is necessary for the performance of their duties.</p><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',6),t=[r];function i(l,p,c,d,f,u){return a(),o("div",null,t)}const y=e(s,[["render",i]]);export{m as __pageData,y as default};
