import{_ as a,o as e,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 4.1-1101.1 Adult sharing of marijuana.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-1101.1.md","filePath":"vacode/4.1-1101.1.md"}'),t={name:"vacode/4.1-1101.1.md"},n=r('<h1 id="§-4-1-1101-1-adult-sharing-of-marijuana" tabindex="-1">§ 4.1-1101.1 Adult sharing of marijuana. <a class="header-anchor" href="#§-4-1-1101-1-adult-sharing-of-marijuana" aria-label="Permalink to &quot;§ 4.1-1101.1 Adult sharing of marijuana.&quot;">​</a></h1><p>A. For the purposes of this section, &quot;adult sharing&quot; means transferring marijuana between persons who are 21 years of age or older without remuneration. &quot;Adult sharing&quot; does not include instances in which (i) marijuana is given away contemporaneously with another reciprocal transaction between the same parties; (ii) a gift of marijuana is offered or advertised in conjunction with an offer for the sale of goods or services; or (iii) a gift of marijuana is contingent upon a separate reciprocal transaction for goods or services.</p><p>B. Notwithstanding the provisions of § <a href="/vacode/18.2-248.1/">18.2-248.1</a>, no civil or criminal penalty may be imposed for adult sharing of an amount of marijuana that does not exceed one ounce or of an equivalent amount of marijuana products.</p><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',4),i=[n];function s(c,u,d,f,l,h){return e(),o("div",null,i)}const m=a(t,[["render",s]]);export{_ as __pageData,m as default};
