import{_ as e,o as s,c as a,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 4.1-1121 Issuance of summonses for certain offenses; civil penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-1121.md","filePath":"vacode/4.1-1121.md"}'),i={name:"vacode/4.1-1121.md"},o=t('<h1 id="§-4-1-1121-issuance-of-summonses-for-certain-offenses-civil-penalties" tabindex="-1">§ 4.1-1121 Issuance of summonses for certain offenses; civil penalties. <a class="header-anchor" href="#§-4-1-1121-issuance-of-summonses-for-certain-offenses-civil-penalties" aria-label="Permalink to &quot;§ 4.1-1121 Issuance of summonses for certain offenses; civil penalties.&quot;">​</a></h1><p>Any violation under this subtitle that is subject to a civil penalty is a civil offense and shall be charged by summons. A summons for a violation under this subtitle that is subject to a civil penalty may be executed by a law-enforcement officer when such violation is observed by such officer. The summons used by a law-enforcement officer pursuant to this section shall be in a form the same as the uniform summons for motor vehicle law violations as prescribed pursuant to § <a href="/vacode/46.2-388/">46.2-388</a>. Any civil penalties collected pursuant to this subtitle shall be deposited into the Drug Offender Assessment and Treatment Fund established pursuant to § <a href="/vacode/18.2-251.02/">18.2-251.02</a>.</p><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',3),n=[o];function r(c,l,f,u,m,d){return s(),a("div",null,n)}const _=e(i,[["render",r]]);export{p as __pageData,_ as default};
