import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 4.1-113 Board not to regulate certain advertising in the interiors of retail establishments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-113.md","filePath":"vacode/4.1-113.md"}'),i={name:"vacode/4.1-113.md"},o=a('<h1 id="§-4-1-113-board-not-to-regulate-certain-advertising-in-the-interiors-of-retail-establishments" tabindex="-1">§ 4.1-113 Board not to regulate certain advertising in the interiors of retail establishments. <a class="header-anchor" href="#§-4-1-113-board-not-to-regulate-certain-advertising-in-the-interiors-of-retail-establishments" aria-label="Permalink to &quot;§ 4.1-113 Board not to regulate certain advertising in the interiors of retail establishments.&quot;">​</a></h1><p>A. The Board shall not regulate the use of advertising materials or decorations within the premises of a retail on-premises licensee (i) where such advertising materials or decorations cannot be seen from the street or roadway outside of the licensed establishment and (ii) if the retail establishment is located within an enclosed area with no street or roadway, where such advertising or decorations cannot be seen more than fifteen feet from the nearest window.</p><p>B. This section shall not restrict the regulation of advertising materials or decorations containing references to an alcoholic beverage brand or manufacturer, except the Board shall not regulate such references contained in works of art.</p><p>C. This section shall not restrict or deny the Board its authority pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/4.1-216/" target="_blank" rel="noreferrer">4.1-216</a>, <a href="http://law.lis.virginia.gov/vacode/4.1-317/" target="_blank" rel="noreferrer">4.1-317</a>, and subdivisions 11, 12, 13, 16, and 21 of § <a href="http://law.lis.virginia.gov/vacode/4.1-325/" target="_blank" rel="noreferrer">4.1-325</a>, nor shall this section authorize any manufacturer, bottler, wholesaler or importer of any alcoholic beverages to sell, rent, lend, buy for, or give to any retailer any advertising materials or decorations under any circumstances otherwise prohibited by law.</p><p>1981, c. 574, § 4-69.2; 1993, c. 866.</p>',5),n=[o];function s(l,c,h,d,f,g){return t(),r("div",null,n)}const m=e(i,[["render",s]]);export{_ as __pageData,m as default};
