import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 4.1-1502 Selection of CDFI; Program requirements; guidelines for management of the Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-1502.md","filePath":"vacode/4.1-1502.md"}'),a={name:"vacode/4.1-1502.md"},o=n('<h1 id="§-4-1-1502-selection-of-cdfi-program-requirements-guidelines-for-management-of-the-fund" tabindex="-1">§ 4.1-1502 Selection of CDFI; Program requirements; guidelines for management of the Fund. <a class="header-anchor" href="#§-4-1-1502-selection-of-cdfi-program-requirements-guidelines-for-management-of-the-fund" aria-label="Permalink to &quot;§ 4.1-1502 Selection of CDFI; Program requirements; guidelines for management of the Fund.&quot;">​</a></h1><p>A. The Authority shall establish a Program to provide loans to qualified social equity cannabis licensees for the purpose of promoting business ownership and economic growth by communities that have been disproportionately impacted by the prohibition of cannabis. The Authority shall select and work in collaboration with a CDFI to assist in administering the Program and carrying out the purposes of the Fund. The CDFI selected by the Authority shall have (i) a statewide presence in Virginia, (ii) experience in business lending, (iii) a proven track record of working with disadvantaged communities, and (iv) the capability to dedicate sufficient staff to manage the Program. Working with the selected CDFI, the Authority shall establish monitoring and accountability mechanisms for businesses receiving funding and shall report annually the number of businesses funded; the geographic distribution of the businesses; the costs of the Program; and the outcomes, including the number and types of jobs created.</p><p>B. The Program shall:</p><ol><li><p>Identify social equity qualified cannabis licensees who are in need of capital for the start-up of a cannabis business properly licensed pursuant to the provisions of this subtitle;</p></li><li><p>Provide loans for the purposes described in subsection A;</p></li><li><p>Provide technical assistance; and</p></li><li><p>Bring together community partners to sustain the Program.</p></li></ol><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',5),r=[o];function s(l,c,h,d,u,p){return i(),t("div",null,r)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
