import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 4.1-237 Refund and adjustments on excise tax on beer and wine coolers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-237.md","filePath":"vacode/4.1-237.md"}'),o={name:"vacode/4.1-237.md"},n=t('<h1 id="§-4-1-237-refund-and-adjustments-on-excise-tax-on-beer-and-wine-coolers" tabindex="-1">§ 4.1-237 Refund and adjustments on excise tax on beer and wine coolers. <a class="header-anchor" href="#§-4-1-237-refund-and-adjustments-on-excise-tax-on-beer-and-wine-coolers" aria-label="Permalink to &quot;§ 4.1-237 Refund and adjustments on excise tax on beer and wine coolers.&quot;">​</a></h1><p>A. Whenever it is proved to the satisfaction of the Board that the tax levied pursuant to § <a href="http://law.lis.virginia.gov/vacode/4.1-236/" target="_blank" rel="noreferrer">4.1-236</a> has been paid and that beer or wine coolers were or are (i) damaged, destroyed or otherwise deemed to be unsalable by reason of fire or any other providential cause before sale to the consumer, (ii) destroyed voluntarily because the beer or wine coolers were defective and after notice to and approval by the Board of such destruction or (iii) destroyed in any manner while in possession of a common, private or contract carrier, the Board shall certify such facts to the Comptroller for approval of a refund payment from the state treasury to such extent as may be proper. The manufacturer, bottler or wholesaler shall make a report thereof to the Board as a portion of the report required by § <a href="http://law.lis.virginia.gov/vacode/4.1-239/" target="_blank" rel="noreferrer">4.1-239</a>.</p><p>B. Whenever it is proved to the satisfaction of the Board that any person has purchased beer or wine coolers which have been sold by him in such manner as to be exempt from the excise tax levied under § <a href="http://law.lis.virginia.gov/vacode/4.1-236/" target="_blank" rel="noreferrer">4.1-236</a>, the Board shall certify such facts to the Comptroller for approval of a refund payment from the state treasury to such extent as may be proper.</p><p>1988, c. 261, §§ 4-141, 4-142; 1993, c. 866.</p>',4),s=[n];function i(d,c,h,l,f,p){return a(),r("div",null,s)}const _=e(o,[["render",i]]);export{m as __pageData,_ as default};
