import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 4.1-238 Bond required to secure excise tax liability on beer and wine coolers, and wine stored in bonded warehouses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-238.md","filePath":"vacode/4.1-238.md"}'),r={name:"vacode/4.1-238.md"},n=a('<h1 id="§-4-1-238-bond-required-to-secure-excise-tax-liability-on-beer-and-wine-coolers-and-wine-stored-in-bonded-warehouses" tabindex="-1">§ 4.1-238 Bond required to secure excise tax liability on beer and wine coolers, and wine stored in bonded warehouses. <a class="header-anchor" href="#§-4-1-238-bond-required-to-secure-excise-tax-liability-on-beer-and-wine-coolers-and-wine-stored-in-bonded-warehouses" aria-label="Permalink to &quot;§ 4.1-238 Bond required to secure excise tax liability on beer and wine coolers, and wine stored in bonded warehouses.&quot;">​</a></h1><p>A. Every manufacturer, bottler, or wholesaler, as a condition precedent to obtaining a license to sell beer or wine coolers to a licensed retailer, shall file a bond with the Board in such sum and with such surety as the Board deems adequate to cover the tax liability of each such manufacturer, bottler, or wholesaler. The sum of such bond shall be proportioned to the volume of business of each such manufacturer, bottler, or wholesaler, but shall in no event be less than $1,000 or more than $100,000. Such bond shall be conditioned upon the payment by such manufacturer, bottler, or wholesaler of the tax imposed by § <a href="/vacode/4.1-236/">4.1-236</a>.</p><p>B. Every holder of a bonded warehouse permit, issued in accordance with subdivision 13 of § <a href="/vacode/4.1-212/">4.1-212</a>, as a condition to obtaining the permit, shall file a bond with the Board in such sum and with such surety as the Board deems adequate to cover the tax liability of each such permittee. The sum of such bond shall be proportioned to the volume of business of each such manufacturer, bottler, or wholesaler, but shall in no event be less than $1,000 or more than $10,000. Such bond shall be conditioned upon the payment by the permittee of the tax imposed by § <a href="/vacode/4.1-234/">4.1-234</a>.</p><p>C. The Board may waive the requirement of both the surety and the bond, in cases where a manufacturer, bottler, or wholesaler has previously demonstrated his financial responsibility.</p><p>D. Upon the termination of the bond, its guaranty or surety, the Board, upon reasonable notice to the manufacturer, bottler, or wholesaler so licensed, may suspend the license so granted until such times as the required bond is filed or the proper surety or guaranty is given.</p><p>1988, c. 261, § 4-138; 1993, c. 866; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0564" target="_blank" rel="noreferrer">564</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1113" target="_blank" rel="noreferrer">1113</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1114" target="_blank" rel="noreferrer">1114</a>.</p>',6),i=[n];function s(l,d,h,c,u,b){return o(),t("div",null,i)}const m=e(r,[["render",s]]);export{f as __pageData,m as default};
