import{_ as e,o as r,c as i,O as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 4.1-310.1 Delivery of wine or beer to retail licensee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-310.1.md","filePath":"vacode/4.1-310.1.md"}'),t={name:"vacode/4.1-310.1.md"},a=o('<h1 id="§-4-1-310-1-delivery-of-wine-or-beer-to-retail-licensee" tabindex="-1">§ 4.1-310.1 Delivery of wine or beer to retail licensee. <a class="header-anchor" href="#§-4-1-310-1-delivery-of-wine-or-beer-to-retail-licensee" aria-label="Permalink to &quot;§ 4.1-310.1 Delivery of wine or beer to retail licensee.&quot;">​</a></h1><p>Except as otherwise provided in this subtitle or in Board regulation, no wine or beer may be shipped or delivered to a retail licensee for resale unless such wine or beer has first been (i) delivered to the licensed premises of a wine or beer wholesaler and unloaded, (ii) kept on the licensed premises of the wholesaler for not less than four hours prior to reloading on a vehicle, and (iii) recorded in the wholesaler&#39;s inventory. Any holder of a restricted wholesale wine license issued pursuant to subdivision 3 of § <a href="/vacode/4.1-206.2/">4.1-206.2</a> shall be exempt from the requirement set forth in clause (ii).</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0167" target="_blank" rel="noreferrer">167</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1113" target="_blank" rel="noreferrer">1113</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1114" target="_blank" rel="noreferrer">1114</a>.</p>',3),n=[a];function l(s,c,d,h,f,_){return r(),i("div",null,n)}const u=e(t,[["render",l]]);export{v as __pageData,u as default};
