import{_ as e,o,c as s,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 4.1-315 Possession without license to sell alcoholic beverages upon premises of restaurant; exceptions; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-315.md","filePath":"vacode/4.1-315.md"}'),i={name:"vacode/4.1-315.md"},t=a('<h1 id="§-4-1-315-possession-without-license-to-sell-alcoholic-beverages-upon-premises-of-restaurant-exceptions-penalty" tabindex="-1">§ 4.1-315 Possession without license to sell alcoholic beverages upon premises of restaurant; exceptions; penalty. <a class="header-anchor" href="#§-4-1-315-possession-without-license-to-sell-alcoholic-beverages-upon-premises-of-restaurant-exceptions-penalty" aria-label="Permalink to &quot;§ 4.1-315 Possession without license to sell alcoholic beverages upon premises of restaurant; exceptions; penalty.&quot;">​</a></h1><p>A. No alcoholic beverages shall be kept or allowed to be kept upon any premises or upon the person of any proprietor or person employed upon the premises of a restaurant or other place where food or refreshments of any kind are furnished for compensation, except such alcoholic beverages as such person owning or operating such place of business is licensed to purchase and to sell at such place of business.</p><p>B. This section shall not apply to (i) any residence; (ii) alcoholic beverages in the possession of a passenger being transported for compensation as provided in subsection D of § <a href="http://law.lis.virginia.gov/vacode/4.1-308/" target="_blank" rel="noreferrer">4.1-308</a>; (iii) dining areas in restaurants licensed by the Board while such areas are in use for private meetings or parties limited in attendance to members and guests of a particular group, association or organization; (iv) licensed restaurants in office buildings, industrial or similar facilities while such restaurant is closed to the public and is in use for private meetings or parties limited in attendance to employees and nonpaying guests of the owner or a lessee of all or part of such building or facility; or (v) any dining areas or private rooms of residents in an assisted living facility as defined in § <a href="http://law.lis.virginia.gov/vacode/63.2-100/" target="_blank" rel="noreferrer">63.2-100</a> and licensed in accordance with Article 1 (§ <a href="http://law.lis.virginia.gov/vacode/63.2-1800/" target="_blank" rel="noreferrer">63.2-1800</a> et seq.) of Chapter 18 of Title 63.2.</p><p>C. Any person convicted of a violation of this section shall be guilty of a Class 1 misdemeanor.</p><p>Code 1950, p. 876, § 4-61; 1954, c. 512; 1958, c. 270; 1968, c. 7; 1972, c. 168; 1974, c. 497; 1990, c. 932; 1993, c. 866; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0114" target="_blank" rel="noreferrer">114</a>.</p>',5),r=[t];function n(l,c,p,d,h,f){return o(),s("div",null,r)}const v=e(i,[["render",n]]);export{g as __pageData,v as default};
