import{_ as e,o,c as a,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 4.1-324 Illegal sale or keeping of alcoholic beverages by licensees; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-324.md","filePath":"vacode/4.1-324.md"}'),l={name:"vacode/4.1-324.md"},r=i('<h1 id="§-4-1-324-illegal-sale-or-keeping-of-alcoholic-beverages-by-licensees-penalty" tabindex="-1">§ 4.1-324 Illegal sale or keeping of alcoholic beverages by licensees; penalty. <a class="header-anchor" href="#§-4-1-324-illegal-sale-or-keeping-of-alcoholic-beverages-by-licensees-penalty" aria-label="Permalink to &quot;§ 4.1-324 Illegal sale or keeping of alcoholic beverages by licensees; penalty.&quot;">​</a></h1><p>A. No licensee or any agent or employee of such licensee shall:</p><ol><li><p>Sell any alcoholic beverages of a kind other than that which such license or this subtitle authorizes him to sell;</p></li><li><p>Sell beer to which wine, spirits or alcohol has been added, except that a mixed beverage licensee may combine wine or spirits, or both, with beer pursuant to a patron&#39;s order;</p></li><li><p>Sell wine to which spirits or alcohol, or both, have been added, otherwise than as required in the manufacture thereof under Board regulations, except that a mixed beverage licensee may (i) make sangria that contains brandy, triple sec, or other similar spirits and (ii) combine beer or spirits, or both, with wine pursuant to a patron&#39;s order;</p></li><li><p>Sell alcoholic beverages of a kind which such license or this subtitle authorizes him to sell, but to any person other than to those to whom such license or this subtitle authorizes him to sell;</p></li><li><p>Sell alcoholic beverages which such license or this subtitle authorizes him to sell, but in any place or in any manner other than such license or this subtitle authorizes him to sell;</p></li><li><p>Sell any alcoholic beverages when forbidden by this subtitle;</p></li><li><p>Keep or allow to be kept, other than in his residence and for his personal use, any alcoholic beverages other than that which he is authorized to sell by such license or by this subtitle;</p></li><li><p>Sell any beer to a retail licensee, except for cash, if the seller holds a brewery, bottler&#39;s or wholesale beer license;</p></li><li><p>Sell any beer on draft and fail to display to customers the brand of beer sold or misrepresent the brand of any beer sold;</p></li><li><p>Sell any wine for delivery within the Commonwealth to a retail licensee, except for cash, if the seller holds a wholesale wine or farm winery license;</p></li><li><p>Keep or allow to be kept or sell any vaporized form of an alcoholic beverage produced by an alcohol vaporizing device;</p></li><li><p>Keep any alcoholic beverage other than in the bottle or container in which it was purchased by him except: (i) for a frozen alcoholic beverage; and (ii) in the case of wine, in containers of a type approved by the Board pending automatic dispensing and sale of such wine; or</p></li><li><p>Establish any normal or customary pricing of its alcoholic beverages that is intended as a shift or device to evade any &quot;happy hour&quot; regulations adopted by the Board; however, a licensee may increase the volume of an alcoholic beverage sold to a customer if there is a commensurate increase in the normal or customary price charged for the same alcoholic beverage.</p></li></ol><p>B. Any person convicted of a violation of this section shall be guilty of a Class 1 misdemeanor.</p><p>C. Neither this section nor any Board regulation shall prohibit an on-premises restaurant licensee from using alcoholic beverages that the licensee otherwise is authorized to purchase and possess for the purposes of preparing and selling for on-premises consumption food products with a final alcohol content of more than one-half of one percent by volume, as long as such food products are sold to and consumed by persons who are 21 years of age or older.</p><p>Code 1950, § 4-60; 1970, c. 360; 1974, c. 460; 1984, c. 603; 1993, c. 866; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0238" target="_blank" rel="noreferrer">238</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0714" target="_blank" rel="noreferrer">714</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0513" target="_blank" rel="noreferrer">513</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0629" target="_blank" rel="noreferrer">629</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>.</p>',6),t=[r];function s(n,h,c,p,d,b){return o(),a("div",null,t)}const u=e(l,[["render",s]]);export{g as __pageData,u as default};
