import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 4.1-336 Contraband beverages and other articles subject to forfeiture.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/4.1-336.md","filePath":"vacode/4.1-336.md"}'),t={name:"vacode/4.1-336.md"},n=o('<h1 id="§-4-1-336-contraband-beverages-and-other-articles-subject-to-forfeiture" tabindex="-1">§ 4.1-336 Contraband beverages and other articles subject to forfeiture. <a class="header-anchor" href="#§-4-1-336-contraband-beverages-and-other-articles-subject-to-forfeiture" aria-label="Permalink to &quot;§ 4.1-336 Contraband beverages and other articles subject to forfeiture.&quot;">​</a></h1><p>All stills and distilling apparatus and materials for the manufacture of alcoholic beverages, all alcoholic beverages and materials used in their manufacture, all containers in which alcoholic beverages may be found, which are kept, stored, possessed, or in any manner used in violation of the provisions of this subtitle, and any dangerous weapons as described in § <a href="http://law.lis.virginia.gov/vacode/18.2-308/" target="_blank" rel="noreferrer">18.2-308</a>, which may be used, or which may be found upon the person or in any vehicle which such person is using, to aid such person in the unlawful manufacture, transportation or sale of alcoholic beverages, or found in the possession of such person, or any horse, mule or other beast of burden, any wagon, automobile, truck or vehicle of any nature whatsoever which is found in the immediate vicinity of any place where alcoholic beverages are being unlawfully manufactured and which such animal or vehicle is being used to aid in the unlawful manufacture, shall be deemed contraband and shall be forfeited to the Commonwealth.</p><p>Proceedings for the confiscation of the above property shall be in accordance with § <a href="http://law.lis.virginia.gov/vacode/4.1-338/" target="_blank" rel="noreferrer">4.1-338</a> for all such property except motor vehicles which proceedings shall be in accordance with Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2.</p><p>Code 1950, § 4-53; 1954, c. 484; 1993, c. 866; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',4),i=[n];function s(c,l,h,d,f,u){return a(),r("div",null,i)}const g=e(t,[["render",s]]);export{p as __pageData,g as default};
