import{_ as o,o as a,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 40.1-27.1 Discharge of employee for absence due to work-related injury prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-27.1.md","filePath":"vacode/40.1-27.1.md"}'),r={name:"vacode/40.1-27.1.md"},n=e("h1",{id:"§-40-1-27-1-discharge-of-employee-for-absence-due-to-work-related-injury-prohibited",tabindex:"-1"},[t("§ 40.1-27.1 Discharge of employee for absence due to work-related injury prohibited. "),e("a",{class:"header-anchor",href:"#§-40-1-27-1-discharge-of-employee-for-absence-due-to-work-related-injury-prohibited","aria-label":'Permalink to "§ 40.1-27.1 Discharge of employee for absence due to work-related injury prohibited."'},"​")],-1),i=e("p",null,"It shall be an unfair employment practice for an employer who has established an employment policy of discharging employees who are absent from work for a specified number of days to include in the computation of an employee's work absence record any day that such employee is absent from work due to a compensable absence under Title 65.2; provided, that such compensable absences can be calculated into an employee's work record for purposes of discharge after all steps of the excessive absenteeism policy have been exhausted. An employer shall not be held in violation of this section if the employee's absence exceeds six months or if the employer's circumstances have changed during such employee's absence so as to make it impossible or unreasonable not to discharge such employee.",-1),c=e("p",null,"1989, c. 572.",-1),d=[n,i,c];function l(h,p,m,f,u,b){return a(),s("div",null,d)}const k=o(r,[["render",l]]);export{_ as __pageData,k as default};
