import{_ as e,o,c as a,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 40.1-28.01 Nondisclosure or confidentiality agreement; provisions regarding sexual assault; condition of employment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/40.1-28.01.md","filePath":"vacode/40.1-28.01.md"}'),i={name:"vacode/40.1-28.01.md"},t=n('<h1 id="§-40-1-28-01-nondisclosure-or-confidentiality-agreement-provisions-regarding-sexual-assault-condition-of-employment" tabindex="-1">§ 40.1-28.01 Nondisclosure or confidentiality agreement; provisions regarding sexual assault; condition of employment. <a class="header-anchor" href="#§-40-1-28-01-nondisclosure-or-confidentiality-agreement-provisions-regarding-sexual-assault-condition-of-employment" aria-label="Permalink to &quot;§ 40.1-28.01 Nondisclosure or confidentiality agreement; provisions regarding sexual assault; condition of employment.&quot;">​</a></h1><p>A. No employer shall require an employee or a prospective employee to execute or renew any provision in a nondisclosure or confidentiality agreement that has the purpose or effect of concealing the details relating to a claim of sexual assault pursuant to § <a href="/vacode/18.2-61/">18.2-61</a>, <a href="/vacode/18.2-67.1/">18.2-67.1</a>, <a href="/vacode/18.2-67.3/">18.2-67.3</a>, or <a href="/vacode/18.2-67.4/">18.2-67.4</a> as a condition of employment. Any such provision is against public policy and is void and unenforceable.</p><p>B. This section shall in no way limit other grounds that exist at law or in equity for the unenforceability of any such agreement or any provision of such agreement.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0131" target="_blank" rel="noreferrer">131</a>.</p>',4),r=[t];function s(l,c,d,p,u,f){return o(),a("div",null,r)}const h=e(i,[["render",s]]);export{_ as __pageData,h as default};
